import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Search, Filter, Calendar, DollarSign, Tag, Trash2, Edit, AlertTriangle, Plus } from 'lucide-react';
import { toast } from 'react-hot-toast';
import api from '../services/api';
import EditTransactionModal from '../components/EditTransactionModal';

const HistoryContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  color: #007bff;
  margin: 0;
`;

const HeaderActions = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const HeaderActionButton = styled.button`
  padding: 0.75rem 1.5rem;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;
  
  &.danger {
    background: linear-gradient(45deg, #dc3545, #c82333);
    
    &:hover {
      background: linear-gradient(45deg, #c82333, #a71e2a);
      transform: translateY(-2px);
      box-shadow: 0 4px 15px rgba(220, 53, 69, 0.3);
    }
  }
  
  &.success {
    background: linear-gradient(45deg, #28a745, #1e7e34);
    
    &:hover {
      background: linear-gradient(45deg, #1e7e34, #155724);
      transform: translateY(-2px);
      box-shadow: 0 4px 15px rgba(40, 167, 69, 0.3);
    }
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const TransactionCount = styled.div`
  color: #6c757d;
  font-size: 0.9rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const FilterSection = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  padding: 1.5rem;
  border: 2px solid #007bff;
  box-shadow: 0 5px 15px rgba(0, 123, 255, 0.1);
  margin-bottom: 2rem;
`;

const FilterGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr auto;
  gap: 1rem;
  align-items: end;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const FilterGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const Label = styled.label`
  font-weight: 600;
  color: #495057;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-size: 1rem;
  
  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const Select = styled.select`
  padding: 0.75rem;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-size: 1rem;
  background: white;
  
  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const FilterButton = styled.button`
  padding: 0.75rem 1.5rem;
  background: linear-gradient(45deg, #007bff, #0056b3);
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  
  &:hover {
    background: linear-gradient(45deg, #0056b3, #004085);
  }
`;

const TransactionTable = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 15px;
  border: 2px solid #007bff;
  box-shadow: 0 5px 15px rgba(0, 123, 255, 0.1);
  overflow: hidden;
`;

const TableHeader = styled.div`
  display: grid;
  grid-template-columns: 120px 1fr 150px 120px 100px;
  background: linear-gradient(45deg, #007bff, #0056b3);
  color: white;
  padding: 1rem;
  font-weight: 600;
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 0.5rem;
  }
`;

const TableRow = styled.div`
  display: grid;
  grid-template-columns: 120px 1fr 150px 120px 100px;
  padding: 1rem;
  border-bottom: 1px solid #e9ecef;
  align-items: center;
  
  &:hover {
    background: rgba(0, 123, 255, 0.05);
  }
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 0.5rem;
  }
`;

const CategoryBadge = styled.span`
  background: linear-gradient(45deg, #007bff, #0056b3);
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.8rem;
  font-weight: 600;
`;

const AmountText = styled.span`
  font-weight: bold;
  color: #dc3545;
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const TableActionButton = styled.button`
  padding: 0.25rem 0.5rem;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  display: flex;
  align-items: center;
  transition: all 0.3s ease;
  
  &.edit {
    background: #ffc107;
    color: white;
    
    &:hover {
      background: #e0a800;
      transform: translateY(-1px);
      box-shadow: 0 2px 8px rgba(255, 193, 7, 0.3);
    }
  }
  
  &.delete {
    background: #dc3545;
    color: white;
    
    &:hover {
      background: #c82333;
      transform: translateY(-1px);
      box-shadow: 0 2px 8px rgba(220, 53, 69, 0.3);
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: #6c757d;
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 15px;
  padding: 2rem;
  max-width: 500px;
  width: 90%;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
`;

const ModalHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1.5rem;
  color: #dc3545;
`;

const ModalTitle = styled.h3`
  margin: 0;
  color: #dc3545;
`;

const ModalText = styled.p`
  color: #495057;
  line-height: 1.6;
  margin-bottom: 1.5rem;
`;

const ConfirmInput = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 2px solid #e9ecef;
  border-radius: 8px;
  font-size: 1rem;
  margin-bottom: 1.5rem;
  
  &:focus {
    outline: none;
    border-color: #dc3545;
  }
`;

const ModalActions = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: flex-end;
`;

const ModalButton = styled.button`
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  
  &.cancel {
    background: #6c757d;
    color: white;
    
    &:hover {
      background: #5a6268;
    }
  }
  
  &.confirm {
    background: #dc3545;
    color: white;
    
    &:hover {
      background: #c82333;
    }
    
    &:disabled {
      opacity: 0.6;
      cursor: not-allowed;
    }
  }
`;

const categories = [
  { value: '', label: 'Táº¥t cáº£ danh má»¥c' },
  { value: 'an uong', label: 'ğŸ½ï¸ Ä‚n uá»‘ng' },
  { value: 'di lai', label: 'ğŸš— Äi láº¡i' },
  { value: 'hoc tap', label: 'ğŸ“š Há»c táº­p' },
  { value: 'giai tri', label: 'ğŸ® Giáº£i trÃ­' },
  { value: 'hoa don', label: 'ğŸ’¡ HÃ³a Ä‘Æ¡n' },
  { value: 'khac', label: 'ğŸ“¦ KhÃ¡c' }
];

const HistoryPage = () => {
  const [transactions, setTransactions] = useState([]);
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showClearModal, setShowClearModal] = useState(false);
  const [confirmText, setConfirmText] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [filters, setFilters] = useState({
    search: '',
    category: '',
    startDate: '',
    endDate: ''
  });

  useEffect(() => {
    loadTransactions();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [transactions, filters]);

  const loadTransactions = async () => {
    try {
      const response = await api.getExpenses();
      setTransactions(response.data);
    } catch (error) {
      console.error('Error loading transactions:', error);
      toast.error('KhÃ´ng thá»ƒ táº£i dá»¯ liá»‡u giao dá»‹ch');
    } finally {
      setLoading(false);
    }
  };

  const applyFilters = () => {
    let filtered = [...transactions];

    // Search filter
    if (filters.search) {
      filtered = filtered.filter(t =>
        t.description.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Category filter
    if (filters.category) {
      filtered = filtered.filter(t => t.category === filters.category);
    }

    // Date filters
    if (filters.startDate) {
      filtered = filtered.filter(t => t.date >= filters.startDate);
    }
    if (filters.endDate) {
      filtered = filtered.filter(t => t.date <= filters.endDate);
    }

    setFilteredTransactions(filtered);
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({ ...prev, [field]: value }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: '',
      startDate: '',
      endDate: ''
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('vi-VN', {
      style: 'currency',
      currency: 'VND'
    }).format(amount);
  };

  const getCategoryLabel = (value) => {
    const category = categories.find(cat => cat.value === value);
    return category ? category.label : value;
  };

  const handleEdit = (transaction) => {
    setEditingTransaction(transaction);
    setShowEditModal(true);
  };

  const handleSaveEdit = async (updatedTransaction) => {
    try {
      await api.updateExpense(updatedTransaction.id, {
        date: updatedTransaction.date,
        amount: updatedTransaction.amount,
        description: updatedTransaction.description,
        category: updatedTransaction.category
      });
      
      // Update local state immediately for better UX
      setTransactions(prev => 
        prev.map(t => 
          t.id === updatedTransaction.id 
            ? { ...t, ...updatedTransaction }
            : t
        )
      );
      
      // Also reload to ensure consistency
      loadTransactions();
    } catch (error) {
      console.error('Error updating transaction:', error);
      throw error; // Re-throw to let modal handle the error
    }
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
    setEditingTransaction(null);
  };

  const handleDelete = async (transaction) => {
    if (!window.confirm(`Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a giao dá»‹ch "${transaction.description}"?`)) {
      return;
    }

    try {
      await api.deleteExpense(transaction.id);
      toast.success('âœ… ÄÃ£ xÃ³a giao dá»‹ch thÃ nh cÃ´ng!');

      // Reload transactions
      loadTransactions();
    } catch (error) {
      console.error('Error deleting transaction:', error);
      toast.error('âŒ CÃ³ lá»—i xáº£y ra khi xÃ³a giao dá»‹ch');
    }
  };

  const handleClearAll = () => {
    setShowClearModal(true);
    setConfirmText('');
  };

  const confirmClearAll = async () => {
    if (confirmText !== "XOA TAT CA") {
      toast.error('âŒ Vui lÃ²ng nháº­p chÃ­nh xÃ¡c "XOA TAT CA" Ä‘á»ƒ xÃ¡c nháº­n');
      return;
    }

    try {
      setLoading(true);
      await api.clearAllExpenses();
      toast.success('âœ… ÄÃ£ xÃ³a táº¥t cáº£ giao dá»‹ch thÃ nh cÃ´ng!');

      // Close modal and reload
      setShowClearModal(false);
      setConfirmText('');
      loadTransactions();
    } catch (error) {
      console.error('Error clearing all transactions:', error);
      toast.error('âŒ CÃ³ lá»—i xáº£y ra khi xÃ³a táº¥t cáº£ giao dá»‹ch');
    } finally {
      setLoading(false);
    }
  };

  const cancelClearAll = () => {
    setShowClearModal(false);
    setConfirmText('');
  };

  const handleCreateSampleData = async () => {
    if (!window.confirm('Táº¡o dá»¯ liá»‡u máº«u Ä‘á»ƒ test á»©ng dá»¥ng?')) {
      return;
    }

    try {
      setLoading(true);
      await api.createSampleData();
      toast.success('âœ… ÄÃ£ táº¡o dá»¯ liá»‡u máº«u thÃ nh cÃ´ng!');

      // Reload transactions
      loadTransactions();
    } catch (error) {
      console.error('Error creating sample data:', error);
      toast.error('âŒ CÃ³ lá»—i xáº£y ra khi táº¡o dá»¯ liá»‡u máº«u');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Äang táº£i...</div>;
  }

  return (
    <HistoryContainer>
      <Header>
        <Title>ğŸ“‹ Lá»‹ch sá»­ Giao dá»‹ch</Title>
        <HeaderActions>
          <TransactionCount>
            <DollarSign size={16} />
            {filteredTransactions.length} giao dá»‹ch
