import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { BarChart3, Plus, Target, FileText, History } from 'lucide-react';

const Nav = styled.nav`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 2px solid #007bff;
  box-shadow: 0 4px 20px rgba(0, 123, 255, 0.1);
  padding: 1rem 2rem;
`;

const NavContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.h1`
  color: #007bff;
  font-size: 1.8rem;
  font-weight: bold;
  margin: 0;
  text-shadow: 0 0 10px rgba(0, 123, 255, 0.3);
`;

const NavLinks = styled.div`
  display: flex;
  gap: 1rem;
`;

const NavLink = styled(Link)`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border-radius: 15px;
  text-decoration: none;
  color: ${props => props.$active ? '#fff' : '#007bff'};
  background: ${props => props.$active 
    ? 'linear-gradient(45deg, #007bff, #0056b3)' 
    : 'transparent'};
  border: 2px solid ${props => props.$active ? '#007bff' : 'transparent'};
  font-weight: 600;
  transition: all 0.3s ease;
  
  &:hover {
    background: ${props => props.$active 
      ? 'linear-gradient(45deg, #0056b3, #004085)' 
      : 'rgba(0, 123, 255, 0.1)'};
    transform: translateY(-2px);
    box-shadow: 0 4px 15px rgba(0, 123, 255, 0.2);
  }
`;

const Navbar = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Dashboard', icon: BarChart3 },
    { path: '/add', label: 'ThÃªm Chi tiÃªu', icon: Plus },
    { path: '/budget', label: 'NgÃ¢n sÃ¡ch', icon: Target },
    { path: '/reports', label: 'BÃ¡o cÃ¡o', icon: FileText },
    { path: '/history', label: 'Lá»‹ch sá»­', icon: History },
  ];

  return (
    <Nav>
      <NavContainer>
        <Logo>ðŸ¤– AI Expense Manager</Logo>
        <NavLinks>
          {navItems.map(({ path, label, icon: Icon }) => (
            <NavLink 
              key={path}
              to={path} 
              $active={location.pathname === path}
            >
              <Icon size={18} />
              {label}
            </NavLink>
          ))}
        </NavLinks>
      </NavContainer>
    </Nav>
  );
};

export default Navbar;
