const categories = [
    { value: 'an uong', label: 'üçΩÔ∏è ƒÇn u·ªëng' },
    { value: 'di lai', label: 'üöó ƒêi l·∫°i' },
    { value: 'hoc tap', label: 'üìö H·ªçc t·∫≠p' },
    { value: 'giai tri', label: 'üéÆ Gi·∫£i tr√≠' },
    { value: 'hoa don', label: 'üí° H√≥a ƒë∆°n' },
    { value: 'khac', label: 'üì¶ Kh√°c' }
];

const EditTransactionModal = ({ isOpen, onClose, transaction, onSave }) => {
    const [formData, setFormData] = useState({
        date: '',
        amount: '',
        description: '',
        category: ''
    });
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    // Initialize form data when transaction changes
    useEffect(() => {
        if (transaction) {
            setFormData({
                date: transaction.date || '',
                amount: transaction.amount?.toString() || '',
                description: transaction.description || '',
                category: transaction.category || ''
            });
            setErrors({});
        }
    }, [transaction]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.date) {
            newErrors.date = 'Vui l√≤ng ch·ªçn ng√†y';
        }

        if (!formData.amount || parseFloat(formData.amount) <= 0) {
            newErrors.amount = 'Vui l√≤ng nh·∫≠p s·ªë ti·ªÅn h·ª£p l·ªá';
        }

        if (!formData.description.trim()) {
            newErrors.description = 'Vui l√≤ng nh·∫≠p m√¥ t·∫£';
        }

        if (!formData.category) {
            newErrors.category = 'Vui l√≤ng ch·ªçn danh m·ª•c';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            toast.error('Vui l√≤ng ki·ªÉm tra l·∫°i th√¥ng tin');
            return;
        }

        setLoading(true);
        try {
            const updatedTransaction = {
                ...transaction,
                date: formData.date,
                amount: parseFloat(formData.amount),
                description: formData.description.trim(),
                category: formData.category
            };

            await onSave(updatedTransaction);
            toast.success('‚úÖ ƒê√£ c·∫≠p nh·∫≠t giao d·ªãch th√†nh c√¥ng!');
            onClose();
        } catch (error) {
            console.error('Error updating transaction:', error);
            toast.error('‚ùå C√≥ l·ªói x·∫£y ra khi c·∫≠p nh·∫≠t giao d·ªãch');
        } finally {
            setLoading(false);
        }
    };

    const handleClose = () => {
        if (!loading) {
            onClose();
        }
    };

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(amount || 0);
    };

    const getCategoryLabel = (value) => {
        const category = categories.find(cat => cat.value === value);
        return category ? category.label : value;
    };

    if (!isOpen || !transaction) return null;

    return (
        <ModalOverlay onClick={(e) => e.target === e.currentTarget && handleClose()}>
            <ModalContainer>
                <ModalHeader>
                    <ModalTitle>
                        <FileText size={20} />
                        Ch·ªânh s·ª≠a giao d·ªãch
                    </ModalTitle>
                    <CloseButton onClick={handleClose} disabled={loading}>
                        <X size={20} />
                    </CloseButton>
                </ModalHeader>

                <ModalBody>
                    <form onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label>
                                <Calendar size={16} />
                                Ng√†y
                            </Label>
                            <Input
                                type="date"
                                name="date"
                                value={formData.date}
                                onChange={handleInputChange}
                                required
                            />
                            {errors.date && (
                                <ValidationMessage>‚ö†Ô∏è {errors.date}</ValidationMessage>
                            )}
                        </FormGroup>

                        <FormGroup>
                            <Label>
                                <DollarSign size={16} />
                                S·ªë ti·ªÅn (VNƒê)
                            </Label>
                            <Input
                                type="number"
                                name="amount"
                                value={formData.amount}
                                onChange={handleInputChange}
                                placeholder="Nh·∫≠p s·ªë ti·ªÅn..."
                                min="0"
                                step="1000"
                                required
                            />
                            {errors.amount && (
                                <ValidationMessage>‚ö†Ô∏è {errors.amount}</ValidationMessage>
                            )}
                        </FormGroup>

                        <FormGroup>
                            <Label>
                                <FileText size={16} />
                                M√¥ t·∫£
                            </Label>
                            <Input
                                type="text"
                                name="description"
                                value={formData.description}
                                onChange={handleInputChange}
                                placeholder="Nh·∫≠p m√¥ t·∫£ giao d·ªãch..."
                                required
                            />
                            {errors.description && (
                                <ValidationMessage>‚ö†Ô∏è {errors.description}</ValidationMessage>
                            )}
                        </FormGroup>

                        <FormGroup>
                            <Label>
                                <Tag size={16} />
                                Danh m·ª•c
                            </Label>
                            <Select
                                name="category"
                                value={formData.category}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="">Ch·ªçn danh m·ª•c...</option>
                                {categories.map(category => (
                                    <option key={category.value} value={category.value}>
                                        {category.label}
                                    </option>
                                ))}
                            </Select>
                            {errors.category && (
                                <ValidationMessage>‚ö†Ô∏è {errors.category}</ValidationMessage>
                            )}
                        </FormGroup>

                        {/* Preview */}
                        {formData.description && formData.amount && (
                            <PreviewCard>
                                <PreviewTitle>üëÅÔ∏è Xem tr∆∞·ªõc giao d·ªãch</PreviewTitle>
                                <PreviewContent>
                                    <PreviewDescription>{formData.description}</PreviewDescription>
                                    <PreviewAmount>{formatCurrency(formData.amount)}</PreviewAmount>
                                </PreviewContent>
                                <PreviewDetails>
                                    <span>{getCategoryLabel(formData.category)}</span>
                                    <span>{formData.date}</span>
                                </PreviewDetails>
                            </PreviewCard>
                        )}

                        <ButtonGroup>
                            <Button
                                type="button"
                                className="secondary"
                                onClick={handleClose}
                                disabled={loading}
                            >
                                H·ªßy b·ªè
                            </Button>
                            <Button
                                type="submit"
                                className="primary"
                                disabled={loading}
                            >
                                <Save size={16} />
                                {loading ? 'ƒêang l∆∞u...' : 'L∆∞u thay ƒë·ªïi'}
                            </Button>
                        </ButtonGroup>
                    </form>
                </ModalBody>
            </ModalContainer>
        </ModalOverlay>
    );
};

export default EditTransactionModal;
